import React from "react";
import { Link } from "react-router-dom";
export default function ProductCard({p}){
  return (
    <div className="card">
      <Link to={`/produto/${p.id}`} style={{width:"100%"}}>
        <img className="img" src={p.imageUrl||"https://via.placeholder.com/400"} alt={p.name}/>
        <h3>{p.name}</h3>
      </Link>
      <div style={{width:"100%",textAlign:"center"}}>
        <div className="old-price">De: R$ {p.oldPrice?p.oldPrice.toFixed(2):"79,90"}</div>
        <div className="price">R$ {p.price?.toFixed(2).replace(".",",")}</div>
        <button className="btn">Comprar</button>
      </div>
    </div>
  );
}
