import React, {useEffect,useState} from "react";
import {useParams,useNavigate} from "react-router-dom";
import api,{setAuthToken} from "../api";
export default function ProductDetails(){
  const {id}=useParams();
  const [p,setP]=useState(null);
  const nav=useNavigate();
  useEffect(()=>{ load(); },[id]);
  async function load(){
    try{ const r=await api.get('/products/'+id); setP(r.data); }catch(e){console.error(e)}
  }
  async function add(){
    try{
      const token=localStorage.getItem('token'); if(!token){ alert('Faça login'); return; }
      setAuthToken(token);
      await api.post('/cart/add',{product:{id:p.id},quantity:1});
      nav('/cart');
    }catch(e){console.error(e); alert('Erro')}
  }
  if(!p) return <div className="container">Carregando...</div>;
  return (
    <div className="container" style={{display:'flex',gap:20,alignItems:'flex-start'}}>
      <img 
  src={p.imageUrl ? `/images/${p.imageUrl}` : 'https://via.placeholder.com/600'}  
  style={{width:420,height:420,objectFit:'cover',borderRadius:10}} 
  alt=""
/>
      <div style={{flex:1}}>
        <h2>{p.name}</h2>
        <div style={{color:'#0a7cff',fontWeight:800,fontSize:22}}>R$ {p.price?.toFixed(2).replace('.',',')}</div>
        <p style={{color:'#666'}}>{p.description}</p>
        <div style={{marginTop:16}}>
          <button className="btn" onClick={add}>Adicionar à sacola</button>
        </div>
      </div>
    </div>
  );
}
