import React, { useEffect, useState } from "react";
import api from "../api";
import ProductCard from "../components/ProductCard";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCat, setActiveCat] = useState(0);

  useEffect(() => {
    loadProducts();
    loadCategories();
  }, []);

  async function loadProducts() {
    try {
      const res = await api.get("/products");
      setProducts(res.data || []);
    } catch (e) {
      console.error(e);
    }
  }

  async function loadCategories() {
    try {
      const res = await api.get("/categories");
      setCategories(res.data || []);
    } catch (e) {
      console.error(e);
    }
  }

  return (
    <div className="home-container">
      <div className="header-row">
        <h2>Produtos</h2>
        <div style={{ color: "#9aa0a6" }}>{products.length} itens</div>
      </div>

      <div className="category-bar">
        <button className={activeCat === 0 ? "cat active" : "cat"} onClick={() => setActiveCat(0)}>Todas</button>
        {categories.map((c) => (
          <button key={c.id} className={activeCat === c.id ? "cat active" : "cat"} onClick={() => setActiveCat(c.id)}>
            {c.name}
          </button>
        ))}
      </div>

      <div className="product-grid">
        {products.filter(p => activeCat === 0 || p.category?.id === activeCat).map((p) => (
          <ProductCard key={p.id} p={p} />
        ))}
      </div>
    </div>
  );
}
