import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";

export default function ProductDetails() {
  const { id } = useParams();
  const [p, setP] = useState(null);
  const nav = useNavigate();

  useEffect(() => {
    load();
  }, [id]);

  async function load() {
    try {
      const r = await api.get("/products/" + id);
      setP(r.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function add() {
    try {
      let sessionId = localStorage.getItem("sessionId");
      if (!sessionId) {
        sessionId = crypto.randomUUID();
        localStorage.setItem("sessionId", sessionId);
      }

      await api.post("/cart/add", {
        sessionId: sessionId,
        product: { id: p.id },
        quantity: 1,
      });

      nav("/cart");
    } catch (e) {
      console.error(e);
      alert("Erro ao adicionar Ã  sacola.");
    }
  }

  if (!p) return <div className="container">Carregando...</div>;

  // --- ðŸ’¥ IMAGENS DO PRODUTO (PRINCIPAL + EXTRAS) ---
  const mainImage = p.imageUrl?.startsWith("http")
    ? p.imageUrl
    : `/images/${p.imageUrl}`;

  const extraImages = Array.isArray(p.images)
    ? p.images.map(img =>
        img.startsWith("http") ? img : `/images/${img}`
      )
    : [];

  return (
    <div
      className="container"
      style={{
        display: "flex",
        alignItems: "flex-start",
        gap: 40,
        paddingTop: 40,
      }}
    >
      {/* COLUNA DA ESQUERDA - GALERIA DE IMAGENS */}
      <div style={{ display: "flex", flexDirection: "column", gap: 10 }}>

        {/* IMAGEM PRINCIPAL */}
        <img
          src={mainImage}
          alt={p.name}
          style={{
            width: 420,
            height: 420,
            objectFit: "contain",
            borderRadius: 10,
            background: "#fff"
          }}
        />

        {/* miniaturas */}
        <div style={{ display: "flex", gap: 10 }}>
          {[mainImage, ...extraImages].map((img, i) => (
            <img
              key={i}
              src={img}
              alt=""
              style={{
                width: 80,
                height: 80,
                objectFit: "contain",
                borderRadius: 6,
                border: "1px solid #ddd",
                cursor: "pointer"
              }}
            />
          ))}
        </div>
      </div>

      {/* COLUNA DA DIREITA - DETALHES */}
      <div style={{ flex: 1 }}>
        <h2 style={{ fontSize: 28, fontWeight: 700 }}>{p.name}</h2>

        <div
          style={{
            color: "#0a7cff",
            fontWeight: 800,
            fontSize: 28,
            marginTop: 10,
            marginBottom: 20,
          }}
        >
          R$ {p.price?.toFixed(2).replace(".", ",")}
        </div>

        <p
          style={{
            fontSize: 16,
            color: "#555",
            lineHeight: "22px",
            maxWidth: 550,
          }}
        >
          {p.description}
        </p>

        <button
          onClick={add}
          className="btn"
          style={{
            padding: "12px 22px",
            fontSize: 17,
            fontWeight: 700,
            borderRadius: 10,
            marginTop: 25,
            width: 220,
          }}
        >
          Adicionar Ã  sacola
        </button>
      </div>
    </div>
  );
}
