import React, {useState, useEffect} from 'react';
import api from '../api';
export default function Admin(){
  const [products,setProducts]=useState([]);
  const [name,setName]=useState('');
  const [price,setPrice]=useState('');
  const [imageUrl,setImageUrl]=useState('');
  useEffect(()=>{ load(); },[]);
  async function load(){ try{ const r=await api.get('/products'); setProducts(r.data);}catch(e){console.error(e);} }
  async function add(){
    try{
      await api.post('/products',{name,price:parseFloat(price||0),imageUrl});
      setName(''); setPrice(''); setImageUrl(''); load();
    }catch(e){console.error(e);}
  }
  return (
    <div style={{padding:20}}>
      <h2>Painel Admin</h2>
      <div>
        <input placeholder="Nome" value={name} onChange={e=>setName(e.target.value)}/>
        <input placeholder="PreÃ§o" value={price} onChange={e=>setPrice(e.target.value)}/>
        <input placeholder="URL imagem" value={imageUrl} onChange={e=>setImageUrl(e.target.value)}/>
        <div className="btn" onClick={add}>Adicionar Produto</div>
      </div>
      <h3>Produtos</h3>
      {products.map(p=>(
        <div key={p.id}>{p.name} - R$ {p.price}</div>
      ))}
    </div>
  );
}
