import React, { useEffect, useState } from "react";
import api from "../api";

export default function Admin() {

  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    carregarProdutos();
  }, []);

  async function carregarProdutos() {
    try {
      const r = await api.get("/products");
      setProdutos(r.data);
    } catch (e) {
      console.error(e);
      alert("Erro ao carregar produtos (talvez sem permissão?)");
    }
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Painel Admin</h2>

      {produtos.length === 0 ? (
        <p>Nenhum produto encontrado.</p>
      ) : (
        produtos.map((p) => (
          <div key={p.id} style={{ borderBottom: "1px solid #ccc", padding: 10 }}>
            <strong>{p.name}</strong> — R$ {p.price}
            <br />
            <small>Categoria: {p.category?.name || "Sem categoria"}</small>
          </div>
        ))
      )}
    </div>
  );
}
