import React, { useState, useEffect } from "react";
import api from "../api";

export default function Admin() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [categoryId, setCategoryId] = useState("");

  useEffect(() => { load(); }, []);

  async function load() {
    try {
      const r = await api.get("/products");
      setProducts(r.data || []);
    } catch (e) {
      console.error(e);
    }
  }

  async function add() {
    try {
      await api.post("/products", {
        name,
        price: parseFloat(price || 0),
        imageUrl,
        category: categoryId ? { id: parseInt(categoryId) } : null
      });
      setName(""); setPrice(""); setImageUrl(""); setCategoryId("");
      load();
    } catch (e) {
      console.error(e);
      alert("Erro ao adicionar");
    }
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Painel Admin</h2>

      <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
        <input placeholder="Nome" value={name} onChange={e => setName(e.target.value)} />
        <input placeholder="Preço" value={price} onChange={e => setPrice(e.target.value)} />
        <input placeholder="URL imagem" value={imageUrl} onChange={e => setImageUrl(e.target.value)} />
        <input placeholder="ID categoria (opcional)" value={categoryId} onChange={e => setCategoryId(e.target.value)} />
        <div className="btn" onClick={add}>Adicionar</div>
      </div>

      <h3>Produtos</h3>
      {products.map(p => (
        <div key={p.id} style={{ padding: 8, borderBottom: "1px solid rgba(255,255,255,0.04)" }}>
          {p.name} — R$ {p.price}
        </div>
      ))}
    </div>
  );
}
