import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import useAuth from "../hooks/useAuth";

export default function Navbar() {
  const [search, setSearch] = useState("");
  const navigate = useNavigate();
  const auth = useAuth();

  function handleSearch(e) {
    if (e.key === "Enter" && search.trim() !== "") {
      navigate("/buscar?q=" + search);
    }
  }

  return (
    <>
      <div className="topbar">
        <div>Pedido m√≠nimo: R$ 10,00</div>
        <div>Estabelecimento aberto</div>
      </div>

      <header className="header">
        <div className="header-inner">
          <div className="logo">
            <img
              src="/images/logo.jpeg"
              alt="logo"
              style={{
                height: 150,
                width: 150,
                objectFit: "cover",
                borderRadius: "50%",
                marginRight: 12,
              }}
            />

            <div>
              <h1 style={{ fontSize: "1.8rem", margin: 0 }}>DeepSurvey</h1>
              <small style={{ fontSize: "1rem" }}>Suplementos.</small>
            </div>
          </div>

          {/* üîç CAMPO DE PESQUISA */}
          <div className="searchbox">
            <input
              placeholder="Digite sua busca..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={handleSearch}
            />
          </div>

          <div style={{ display: "flex", gap: 12 }}>
            <Link to="/cart">Minha sacola</Link>

            {/* üî• SE FOR ADMIN, MOSTRA O BOT√ÉO ADMIN */}
            {auth.admin && <Link to="/admin">Admin</Link>}

            <Link to="/login">Login</Link>
          </div>
        </div>
      </header>

      <nav className="navbar">
        <Link to="/">In√≠cio</Link>
        <Link to="/categorias">Categorias</Link>
        <Link to="/ofertas">Ofertas</Link>
        <Link to="/faleconosco">Fale conosco</Link>
      </nav>
    </>
  );
}
