import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api, { setAuthToken } from "../api";
import "../styles.css";

export default function ProductDetails() {
  const { id } = useParams();
  const [produto, setProduto] = useState(null);

  useEffect(() => {
    carregarProduto();
  }, []);

  async function carregarProduto() {
    try {
      const res = await api.get(`/products/${id}`);
      setProduto(res.data);
    } catch (err) {
      console.error("Erro ao carregar produto", err);
    }
  }

  async function adicionarAoCarrinho() {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        return alert("Você precisa fazer login antes de adicionar ao carrinho!");
      }

      setAuthToken(token);

      await api.post("/cart/add", {
        product: { id: produto.id },
        quantity: 1,
      });

      alert("Produto adicionado ao carrinho!");
    } catch (err) {
      console.error("Erro ao adicionar item:", err);
      alert("Erro ao adicionar ao carrinho.");
    }
  }

  if (!produto) {
    return <p style={{ padding: 30 }}>Carregando produto...</p>;
  }

  return (
    <div className="product-details">
      <img
        src={produto.imageUrl || "https://via.placeholder.com/500"}
        alt={produto.name}
      />

      <div className="details-box">
        <h2 className="details-title">{produto.name}</h2>

        <div className="details-price">
          R$ {produto.price.toFixed(2).replace(".", ",")}
        </div>

        <p className="details-description">
          {produto.description || "Nenhuma descrição disponível."}
        </p>

        <div style={{ marginTop: 30 }}>

          {/* COMPRAR AGORA (Checkout será implementado depois) */}
          <button className="btn" style={{ width: 220, marginBottom: 15 }}>
            Comprar agora
          </button>

          {/* ADICIONAR AO CARRINHO */}
          <button
            className="btn"
            style={{
              width: 220,
              background: "#333",
              color: "#fff",
            }}
            onClick={adicionarAoCarrinho}
          >
            Adicionar ao carrinho
          </button>

        </div>
      </div>
    </div>
  );
}
