import React, {useEffect,useState} from 'react';
import api from '../api';
export default function Cart(){
  const [items,setItems]=useState([]);
  useEffect(()=>{ load(); },[]);
  async function load(){ try{ const r=await api.get('/cart'); setItems(r.data);}catch(e){console.error(e);} }
  const total = items.reduce((s,i)=>s + (i.product?.price||0) * (i.quantity||1),0);
  return (
    <div className="cart-container">
      <h2>Meu Carrinho</h2>
      {items.length===0 ? <p>Seu carrinho est√° vazio</p> : (
        <>
          {items.map(it=>(
            <div key={it.id} className="cart-item">
              <img src={it.product?.imageUrl||'https://via.placeholder.com/120'} alt=""/>
              <div style={{flex:1}}>
                <h4>{it.product?.name}</h4>
                <div>Qtd: {it.quantity}</div>
              </div>
            </div>
          ))}
          <h3>Total: R$ {total.toFixed(2).replace('.',',')}</h3>
          <a href="/checkout"><div className="btn">Finalizar</div></a>
        </>
      )}
    </div>
  );
}
