import React, { useEffect, useState } from "react";
import api from "../api";
import ProdutoForm from "../components/ProdutoForm";
import CategoriaForm from "../components/CategoriaForm";

export default function Admin() {

  const [produtos, setProdutos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [editando, setEditando] = useState(null);

  useEffect(() => {
    carregarTudo();
  }, []);

  async function carregarTudo() {
    try {
      const r1 = await api.get("/products");
      const r2 = await api.get("/categories");
      setProdutos(r1.data);
      setCategorias(r2.data);
    } catch (e) {
      alert("Erro ao carregar dados (sem permissão?)");
      console.error(e);
    }
  }

  async function excluirProduto(id) {
    if (!window.confirm("Deseja excluir este produto?")) return;

    await api.delete(`/products/${id}`);
    carregarTudo();
  }

  async function excluirCategoria(id) {
    if (!window.confirm("Excluir categoria (atenção: produtos ligados serão afetados)?")) return;

    await api.delete(`/categories/${id}`);
    carregarTudo();
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>Painel Admin</h1>

      <hr />

      <h2>Gerenciar Produtos</h2>

      <ProdutoForm
        categorias={categorias}
        produtoEdit={editando}
        onSave={() => { setEditando(null); carregarTudo(); }}
      />

      <hr />

      <h3>Lista de Produtos</h3>

      {produtos.length === 0 ? (
        <p>Nenhum produto cadastrado.</p>
      ) : (
        produtos.map((p) => (
          <div
            key={p.id}
            style={{
              padding: 10,
              borderBottom: "1px solid #ccc",
              marginBottom: 10
            }}
          >
            <strong>{p.name}</strong> — R$ {p.price}
            <br />
            <small>Categoria: {p.category?.name || "Sem categoria"}</small>
            <br />

            {p.onSale && (
              <span style={{ color: "red" }}>Promoção: R$ {p.salePrice}</span>
            )}

            <br /><br />

            <button onClick={() => setEditando(p)}>Editar</button>
            <button
              style={{ marginLeft: 10, color: "red" }}
              onClick={() => excluirProduto(p.id)}
            >
              Excluir
            </button>
          </div>
        ))
      )}

      <hr />

      <h2>Categorias</h2>

      <CategoriaForm
        onSave={carregarTudo}
      />

      <h3>Lista de Categorias</h3>

      {categorias.map((c) => (
        <div
          key={c.id}
          style={{
            padding: 8,
            borderBottom: "1px solid #ccc"
          }}
        >
          {c.name}
          <button
            style={{ marginLeft: 10, color: "red" }}
            onClick={() => excluirCategoria(c.id)}
          >
            Excluir
          </button>
        </div>
      ))}

    </div>
  );
}
