import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import { setAuthToken } from "../api";
import { useStoreConfig } from "../context/StoreConfigContext";

export default function Navbar() {
  const [search, setSearch] = useState("");
  const navigate = useNavigate();
  const auth = useAuth();
  const { cfg } = useStoreConfig(); // ← pega configs do painel

  function handleSearch(e) {
    if (e.key === "Enter" && search.trim() !== "") {
      navigate("/buscar?q=" + search);
    }
  }

  function logout() {
    setAuthToken(null);
    window.location.reload();
  }

  return (
    <>
      {/* BARRA SUPERIOR */}
      <div
        className="topbar"
        style={{
          background: cfg?.themeColor || "#2c2b6e",
          color: "white",
        }}
      >
        <div>
          Pedido mínimo: R$ {cfg?.minimumOrderValue?.toFixed(2).replace(".", ",")}
        </div>

        <div>
          {cfg?.storeOpen ? "Estabelecimento aberto" : "Fechado"}
        </div>
      </div>

      {/* CABEÇALHO */}
      <header className="header">
        <div className="header-inner">

          {/* LOGO + NOME DA LOJA */}
          <div className="logo">
            <img
              src={cfg?.logoUrl || "/images/logo.jpeg"}
              alt="logo"
              style={{
                height: 150,
                width: 150,
                objectFit: "cover",
                borderRadius: "50%",
                marginRight: 12,
              }}
            />

            <div>
              <h1 style={{ fontSize: "1.8rem", margin: 0 }}>
                {cfg?.storeName || "Minha Loja"}
              </h1>
              <small>Suplementos.</small>
            </div>
          </div>

          {/* BARRA DE BUSCA */}
          <div className="searchbox">
            <input
              placeholder="Digite sua busca..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={handleSearch}
            />
          </div>

          {/* LINKS DIREITA */}
          <div style={{ display: "flex", gap: 12 }}>
            <Link to="/cart">Minha sacola</Link>

            {/* VISÍVEL APENAS PARA ADMIN */}
            {auth.admin && (
              <>
                <Link to="/painel">Painel</Link>
                <Link to="/painel-loja">Config. Loja</Link>
              </>
            )}

            {/* BOTÃO DE SAIR */}
            {auth.logged && (
              <span
                style={{ cursor: "pointer", color: "#c00" }}
                onClick={logout}
              >
                Sair
              </span>
            )}
          </div>

        </div>
      </header>

      {/* MENU INFERIOR */}
      <nav className="navbar">
        <Link to="/">Início</Link>
        <Link to="/categorias">Categorias</Link>
        <Link to="/ofertas">Ofertas</Link>
        <Link to="/faleconosco">Fale conosco</Link>
      </nav>
    </>
  );
}
