import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import { setAuthToken } from "../api";

export default function Navbar() {
  const [search, setSearch] = useState("");
  const navigate = useNavigate();
  const auth = useAuth();

  function handleSearch(e) {
    if (e.key === "Enter" && search.trim() !== "") {
      navigate("/buscar?q=" + search);
    }
  }

  function logout() {
    setAuthToken(null);
    window.location.reload();
  }

  return (
    <>
      <div className="topbar">
        <div>Pedido mínimo: R$ 10,00</div>
        <div>Estabelecimento aberto</div>
      </div>

      <header className="header">
        <div className="header-inner">
          <div className="logo">
            <img
              src="/images/logo.jpeg"
              alt="logo"
              style={{
                height: 150,
                width: 150,
                objectFit: "cover",
                borderRadius: "50%",
                marginRight: 12,
              }}
            />

{isAdmin && (
  <>
    <Link to="/painel">Painel</Link>
    <Link to="/painel-loja">Config. Loja</Link>
  </>
)}


            <div>
              <h1 style={{ fontSize: "1.8rem", margin: 0 }}>DeepSurvey</h1>
              <small style={{ fontSize: "1rem" }}>Suplementos.</small>
            </div>
          </div>

          <div className="searchbox">
            <input
              placeholder="Digite sua busca..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={handleSearch}
            />
          </div>

          <div style={{ display: "flex", gap: 12 }}>
            <Link to="/cart">Minha sacola</Link>

            {/* Painel só se for admin */}
            {auth.admin && <Link to="/painel">Painel</Link>}

            {/* Sair só logado */}
            {auth.logged && (
              <span
                style={{ cursor: "pointer", color: "#c00" }}
                onClick={logout}
              >
                Sair
              </span>
            )}
          </div>
        </div>
      </header>

      <nav className="navbar">
        <Link to="/">Início</Link>
        <Link to="/categorias">Categorias</Link>
        <Link to="/ofertas">Ofertas</Link>
        <Link to="/faleconosco">Fale conosco</Link>
      </nav>
    </>
  );
}
