import React from 'react';
import { Link } from 'react-router-dom';
export default function ProductCard({p}){
  return (
    <Link to={`/produto/${p.id}`} className="product-card">
      <img className="product-img" src={p.imageUrl||'https://via.placeholder.com/400'} alt={p.name}/>
      <h3>{p.name}</h3>
      <div className="price">R$ {p.price?.toFixed(2).replace('.',',')}</div>
      <div className="btn">Ver produto</div>
    </Link>
  );
}
