import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api";
import "../styles.css";

export default function ProductDetails() {
  const { id } = useParams();
  const [produto, setProduto] = useState(null);

  useEffect(() => {
    carregarProduto();
  }, []);

  async function carregarProduto() {
    try {
      const res = await api.get(`/products/${id}`);
      setProduto(res.data);
    } catch (err) {
      console.error("Erro ao carregar produto", err);
    }
  }

  if (!produto) return <p style={{ padding: 30 }}>Carregando produto...</p>;

  return (
    <div className="product-details">
      <img
        src={produto.imageUrl || "https://via.placeholder.com/400"}
        alt={produto.name}
      />

      <div className="details-box">
        <h2 className="details-title">{produto.name}</h2>

        <div className="details-price">
          R$ {produto.price.toFixed(2).replace(".", ",")}
        </div>

        <p className="details-description">
          {produto.description || "Sem descrição disponível."}
        </p>

        <div style={{ marginTop: 25 }}>
          <button className="btn" style={{ width: "200px", marginBottom: 12 }}>
            Comprar agora
          </button>

          <br />

          <button
            className="btn"
            style={{ width: "200px", background: "#333", color: "#fff" }}
          >
            Adicionar ao carrinho
          </button>
        </div>
      </div>
    </div>
  );
}
