import React, { useEffect, useState } from "react";
import api, { setAuthToken } from "../api";
import "../styles.css";

export default function Cart() {
  const [itens, setItens] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) setAuthToken(token);
    carregarCarrinho();
  }, []);

  async function carregarCarrinho() {
    try {
      const res = await api.get("/cart");
      setItens(res.data);
    } catch (err) {
      console.error("Erro ao carregar carrinho", err);
    }
  }

  async function limparCarrinho() {
    try {
      await api.post("/cart/clear");
      setItens([]);
    } catch (err) {
      console.error("Erro ao limpar carrinho", err);
    }
  }

  const total = itens.reduce(
    (sum, item) => sum + item.product.price * item.quantity,
    0
  );

  if (!token)
    return (
      <div className="cart-container">
        <h2>Faça login para acessar seu carrinho</h2>
      </div>
    );

  return (
    <div className="cart-container">
      <h2 className="cart-title">Meu Carrinho</h2>

      {itens.length === 0 ? (
        <p style={{ marginTop: 20 }}>Seu carrinho está vazio.</p>
      ) : (
        <>
          {itens.map((item) => (
            <div className="cart-item" key={item.id}>
              <img
                src={item.product.imageUrl || "https://via.placeholder.com/120"}
                alt={item.product.name}
              />

              <div style={{ flex: 1 }}>
                <h3>{item.product.name}</h3>
                <p className="cart-price">
                  R$ {item.product.price.toFixed(2).replace(".", ",")}
                </p>
                <p>Quantidade: {item.quantity}</p>
              </div>
            </div>
          ))}

          <div className="cart-total">
            Total: <strong>R$ {total.toFixed(2).replace(".", ",")}</strong>
          </div>

          <button className="btn" style={{ width: 220, marginTop: 15 }}>
            Finalizar Pedido
          </button>

          <button
            className="btn"
            style={{
              width: 220,
              marginTop: 15,
              background: "#333",
              color: "#fff",
            }}
            onClick={limparCarrinho}
          >
            Limpar Carrinho
          </button>
        </>
      )}
    </div>
  );
}
