import React, { useState, useEffect } from "react";
import api from "../api";

export default function Admin() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  // Campos do formulário
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [stock, setStock] = useState("");

  useEffect(() => {
    loadProducts();
    loadCategories();
  }, []);

  async function loadProducts() {
    try {
      const r = await api.get("/products");
      setProducts(r.data);
    } catch (e) {
      alert("Erro ao carregar produtos");
    }
  }

  async function loadCategories() {
    try {
      const r = await api.get("/categories");
      setCategories(r.data);
    } catch {}
  }

  async function addProduct() {
    if (!name || !price) {
      alert("Preencha nome e preço");
      return;
    }

    try {
      await api.post("/products", {
        name,
        price: parseFloat(price),
        description,
        stock: parseInt(stock || "0"),
        imageUrl,
        category: categoryId ? { id: categoryId } : null
      });

      setName("");
      setPrice("");
      setDescription("");
      setImageUrl("");
      setStock("");
      setCategoryId("");

      loadProducts();
    } catch (e) {
      console.error(e);
      alert("Erro ao adicionar");
    }
  }

  async function deleteProduct(id) {
    if (!window.confirm("Excluir produto?")) return;

    try {
      await api.delete("/products/" + id);
      loadProducts();
    } catch {
      alert("Erro ao excluir");
    }
  }

  return (
    <div className="container">
      <h2>Painel Admin</h2>

      {/* Formulário */}
      <div style={{ display: "flex", flexDirection: "column", gap: 8, maxWidth: 400 }}>

        <input placeholder="Nome" value={name} onChange={e => setName(e.target.value)} />

        <input placeholder="Preço" type="number" value={price} onChange={e => setPrice(e.target.value)} />

        <input placeholder="Estoque" type="number" value={stock} onChange={e => setStock(e.target.value)} />

        <input placeholder="Imagem URL (/images/x.jpeg)" value={imageUrl} onChange={e => setImageUrl(e.target.value)} />

        <textarea placeholder="Descrição" value={description} onChange={e => setDescription(e.target.value)} />

        <select value={categoryId} onChange={e => setCategoryId(e.target.value)}>
          <option value="">Selecione a categoria</option>
          {categories.map(c => (
            <option key={c.id} value={c.id}>{c.name}</option>
          ))}
        </select>

        <button className="btn" onClick={addProduct}>Adicionar</button>
      </div>

      <hr />

      {/* Lista de produtos */}
      {products.map(p => (
        <div key={p.id} style={{ borderBottom: "1px solid #ddd", padding: 10 }}>
          <strong>{p.name}</strong> — R$ {p.price?.toFixed(2)}
          <br />
          Categoria: {p.category?.name || "Sem categoria"}
          <br />
          <button onClick={() => deleteProduct(p.id)} style={{ color: "red" }}>Excluir</button>
        </div>
      ))}
    </div>
  );
}
