import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api, { setAuthToken } from "../api";

export default function ProductDetails() {
  const { id } = useParams();
  const [produto, setProduto] = useState(null);
  const nav = useNavigate();

  useEffect(() => {
    carregar();
  }, [id]);

  async function carregar() {
    try {
      const r = await api.get(`/products/${id}`);
      setProduto(r.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function adicionarAoCarrinho() {
    try {
      const token = localStorage.getItem("token");
      if (!token) return alert("Faça login para adicionar ao carrinho.");
      setAuthToken(token);
      await api.post("/cart/add", { product: { id: produto.id }, quantity: 1 });
      alert("Adicionado ao carrinho");
      nav("/cart");
    } catch (e) {
      console.error(e);
      alert("Erro ao adicionar ao carrinho");
    }
  }

  if (!produto) return <div style={{ padding: 24 }}>Carregando...</div>;

  return (
    <div className="product-details">
      <img src={produto.imageUrl || "https://via.placeholder.com/700"} alt={produto.name} />
      <div className="details-box">
        <h2 className="details-title">{produto.name}</h2>
        <div className="details-price">R$ {produto.price?.toFixed(2).replace(".", ",")}</div>
        <p className="details-description">{produto.description || "Sem descrição."}</p>

        <div style={{ marginTop: 18 }}>
          <button className="btn" style={{ marginRight: 12 }}>Comprar agora</button>
          <button className="btn ghost" onClick={adicionarAoCarrinho} style={{ background: "#111", color: "#fff", border: "1px solid rgba(255,255,255,0.04)" }}>
            Adicionar ao carrinho
          </button>
        </div>
      </div>
    </div>
  );
}
