import React, {useEffect,useState} from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';
export default function ProductDetails(){
  const { id } = useParams();
  const [produto,setProduto]=useState(null);
  useEffect(()=>{ load(); },[]);
  async function load(){
    try{ const r=await api.get(`/products/${id}`); setProduto(r.data); }catch(e){console.error(e);}
  }
  if(!produto) return <div style={{padding:20}}>Carregando...</div>;
  return (
    <div className="product-details">
      <img src={produto.imageUrl||'https://via.placeholder.com/500'} alt={produto.name}/>
      <div className="details-box">
        <h2 className="details-title">{produto.name}</h2>
        <div className="details-price">R$ {produto.price?.toFixed(2).replace('.',',')}</div>
        <p className="details-description">{produto.description}</p>
        <div style={{marginTop:20}}>
          <a href="/cart"><div className="btn">Ir para carrinho</div></a>
        </div>
      </div>
    </div>
  );
}
