import React, { useEffect, useState } from "react";
import api from "../api";

export default function Cart() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    load();
  }, []);

  async function load() {
    try {
      const sessionId = localStorage.getItem("sessionId");
      if (!sessionId) return;

      const r = await api.get("/cart/" + sessionId);
      setItems(r.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function updateQty(id, newQty) {
    if (newQty <= 0) return;

    try {
      await api.post("/cart/add", {
        id: id,
        product: { id: items.find(i => i.id === id).product.id },
        quantity: newQty
      });
      load();
    } catch (err) {
      console.error(err);
    }
  }

  async function removeItem(id) {
    try {
      await api.post("/cart/remove", { itemId: id });
      load();
    } catch (e) {
      console.error(e);
    }
  }

  return (
    <div className="container" style={{ paddingTop: 40 }}>
      <h2 style={{ marginBottom: 30 }}>Minha sacola</h2>

      {items.length === 0 && (
        <div style={{ fontSize: 18, color: "#666" }}>Sua sacola está vazia.</div>
      )}

      <div style={{ display: "flex", flexDirection: "column", gap: 25 }}>
        {items.map((item) => (
          <div
            key={item.id}
            style={{
              display: "flex",
              gap: 20,
              padding: 15,
              border: "1px solid #eee",
              borderRadius: 10,
              background: "white",
              maxWidth: 650,
            }}
          >

            {/* IMAGEM */}
            <img
              src={
                item.product.imageUrl
                  ? `/images/${item.product.imageUrl}`
                  : "https://via.placeholder.com/120"
              }
              alt={item.product.name}
              style={{
                width: 120,
                height: 120,
                objectFit: "cover",
                borderRadius: 8,
              }}
            />

            {/* INFORMAÇÕES */}
            <div style={{ flex: 1 }}>
              <h4 style={{ margin: 0, marginBottom: 8 }}>
                {item.product.name}
              </h4>

              <div style={{ marginBottom: 10 }}>
                <b>
  R$ {(item.price ? item.price : item.product.price)
      .toFixed(2)
      .replace(".", ",")}
</b>
              </div>

              {/* QUANTIDADE */}
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: 10,
                  marginBottom: 10,
                }}
              >
                <button
                  onClick={() => updateQty(item.id, item.quantity - 1)}
                  style={qtyButton}
                >
                  -
                </button>

                <div style={{ fontSize: 18, fontWeight: 700 }}>
                  {item.quantity}
                </div>

                <button
                  onClick={() => updateQty(item.id, item.quantity + 1)}
                  style={qtyButton}
                >
                  +
                </button>
              </div>

              {/* REMOVER */}
              <button
                onClick={() => removeItem(item.id)}
                style={{
                  padding: "6px 12px",
                  borderRadius: 6,
                  background: "#ff4d4d",
                  color: "white",
                  border: "none",
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Remover
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const qtyButton = {
  width: 30,
  height: 30,
  borderRadius: 6,
  border: "1px solid #ccc",
  background: "#f5f5f5",
  cursor: "pointer",
  fontSize: 18,
  fontWeight: 700,
};
