import React, { useState, useEffect } from "react";
import api from "../api";

export default function Admin() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  // Formulário de novo produto
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [stock, setStock] = useState("");
  const [categoryId, setCategoryId] = useState("");

  // Categorias
  const [newCategory, setNewCategory] = useState("");

  // Produto para edição
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    loadProducts();
    loadCategories();
  }, []);

  // --------------------- CARREGAR DADOS ------------------------
  async function loadProducts() {
    const r = await api.get("/products");
    setProducts(r.data);
  }

  async function loadCategories() {
    const r = await api.get("/categories");
    setCategories(r.data);
  }

  // --------------------- CRIAR PRODUTO -------------------------
  async function addProduct() {
    try {
      await api.post("/products", {
        name,
        price: parseFloat(price),
        description,
        imageUrl,
        stock: parseInt(stock || "0"),
        category: categoryId ? { id: categoryId } : null,
      });

      setName("");
      setPrice("");
      setDescription("");
      setImageUrl("");
      setStock("");
      setCategoryId("");

      loadProducts();
    } catch (e) {
      console.error(e);
      alert("Erro ao adicionar produto");
    }
  }

  // --------------------- EXCLUIR PRODUTO -----------------------
  async function deleteProduct(id) {
    if (!window.confirm("Excluir este produto?")) return;
    await api.delete("/products/" + id);
    loadProducts();
  }

  // --------------------- EDITAR PRODUTO ------------------------
  async function saveEdit() {
    try {
      await api.put("/products/" + editingProduct.id, editingProduct);
      setEditingProduct(null);
      loadProducts();
    } catch (e) {
      console.error(e);
      alert("Erro ao salvar edição");
    }
  }

  // --------------------- CATEGORIAS ----------------------------
  async function addCategory() {
    if (!newCategory) return;

    await api.post("/categories", { name: newCategory });
    setNewCategory("");
    loadCategories();
  }

  async function deleteCategory(id) {
    if (!window.confirm("Excluir categoria?")) return;
    await api.delete("/categories/" + id);
    loadCategories();
  }

  return (
    <div className="container">
      <h2>Painel Admin</h2>

      {/* ==================== ADICIONAR PRODUTO ===================== */}
      <h3>Novo Produto</h3>
      <div style={{ display: "flex", flexDirection: "column", maxWidth: 400, gap: 8 }}>
        <input placeholder="Nome" value={name} onChange={e => setName(e.target.value)} />
        <input placeholder="Preço" type="number" value={price} onChange={e => setPrice(e.target.value)} />
        <input placeholder="Estoque" type="number" value={stock} onChange={e => setStock(e.target.value)} />
        <input placeholder="Imagem URL (/images/xyz.jpeg)" value={imageUrl} onChange={e => setImageUrl(e.target.value)} />
        <textarea placeholder="Descrição" value={description} onChange={e => setDescription(e.target.value)} />

        <select value={categoryId} onChange={e => setCategoryId(e.target.value)}>
          <option value="">Selecione categoria</option>
          {categories.map(c => (
            <option key={c.id} value={c.id}>{c.name}</option>
          ))}
        </select>

        <button onClick={addProduct}>Adicionar Produto</button>
      </div>

      <hr />

      {/* ==================== GERENCIAR CATEGORIAS ===================== */}
      <h3>Categorias</h3>

      <div style={{ display: "flex", gap: 10 }}>
        <input
          placeholder="Nova categoria"
          value={newCategory}
          onChange={e => setNewCategory(e.target.value)}
        />
        <button onClick={addCategory}>Adicionar</button>
      </div>

      <ul>
        {categories.map(c => (
          <li key={c.id}>
            {c.name}
            <button style={{ color: "red", marginLeft: 10 }} onClick={() => deleteCategory(c.id)}>
              Excluir
            </button>
          </li>
        ))}
      </ul>

      <hr />

      {/* ==================== LISTA DE PRODUTOS ===================== */}
      <h3>Produtos</h3>

      {products.map(p => (
        <div key={p.id} style={{ borderBottom: "1px solid #ccc", marginBottom: 10, paddingBottom: 10 }}>
          <strong>{p.name}</strong> — R$ {p.price?.toFixed(2)}
          <br />
          Categoria: {p.category?.name || "Sem categoria"}
          <br />
          <button onClick={() => setEditingProduct({ ...p })}>Editar</button>
          <button style={{ color: "red", marginLeft: 10 }} onClick={() => deleteProduct(p.id)}>
            Excluir
          </button>
        </div>
      ))}

      {/* ==================== MODAL DE EDIÇÃO ===================== */}
      {editingProduct && (
        <div style={{
          position: "fixed",
          top: 0, left: 0,
          width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.6)",
          display: "flex", justifyContent: "center", alignItems: "center"
        }}>
          <div style={{ background: "#fff", padding: 20, width: 400 }}>
            <h3>Editar Produto</h3>

            <input
              value={editingProduct.name}
              onChange={e => setEditingProduct({ ...editingProduct, name: e.target.value })}
            />

            <input
              type="number"
              value={editingProduct.price}
              onChange={e => setEditingProduct({ ...editingProduct, price: parseFloat(e.target.value) })}
            />

            <input
              type="number"
              value={editingProduct.stock}
              onChange={e => setEditingProduct({ ...editingProduct, stock: parseInt(e.target.value) })}
            />

            <input
              value={editingProduct.imageUrl}
              onChange={e => setEditingProduct({ ...editingProduct, imageUrl: e.target.value })}
            />

            <textarea
              value={editingProduct.description}
              onChange={e => setEditingProduct({ ...editingProduct, description: e.target.value })}
            />

            <select
              value={editingProduct.category?.id || ""}
              onChange={e =>
                setEditingProduct({
                  ...editingProduct,
                  category: e.target.value ? { id: e.target.value } : null
                })
              }
            >
              <option value="">Sem categoria</option>
              {categories.map(c => (
                <option key={c.id} value={c.id}>{c.name}</option>
              ))}
            </select>

            <br /><br />

            <button onClick={saveEdit}>Salvar</button>
            <button style={{ marginLeft: 10 }} onClick={() => setEditingProduct(null)}>
              Cancelar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
