import React, { useEffect, useState } from "react";
import api, { setAuthToken } from "../api";

export default function Cart() {

  const [items, setItems] = useState([]);

  useEffect(() => {
    load();
  }, []);

  async function load() {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      setAuthToken(token);
      const r = await api.get("/cart");
      setItems(r.data);
    } catch (e) {
      console.error(e);
    }
  }

  return (
    <div className="container">
      <h2>Minha sacola</h2>

      {items.length === 0 && (
        <p>Sua sacola est√° vazia.</p>
      )}

      {items.map(ci => (
        <div key={ci.id} className="cart-item">
          <img src={`/images/${ci.product.imageUrl}`} alt="" />
          <div>
            <strong>{ci.product.name}</strong>
            <div>Qtd: {ci.quantity}</div>
            <div>R$ {ci.product.price.toFixed(2)}</div>
          </div>
        </div>
      ))}

    </div>
  );
}
