import React, { useEffect, useState } from "react";
import api from "../api";

export default function Cart() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    loadCart();
  }, []);

  async function loadCart() {
    try {
      let sessionId = localStorage.getItem("sessionId");
      if (!sessionId) return; // nunca deve acontecer

      const r = await api.get("/cart", {
        params: { sessionId },
      });

      setItems(r.data);
    } catch (e) {
      console.error("Erro ao carregar carrinho:", e);
    }
  }

  async function removeItem(id) {
    try {
      let sessionId = localStorage.getItem("sessionId");

      await api.post("/cart/remove", {
        sessionId,
        itemId: id,
      });

      loadCart();
    } catch (e) {
      console.error(e);
    }
  }

  if (items.length === 0)
    return (
      <div className="container">
        <h2>Minha sacola</h2>
        <p>Sua sacola está vazia.</p>
      </div>
    );

  return (
    <div className="container">
      <h2>Minha sacola</h2>

      {items.map((i) => (
        <div
          key={i.id}
          style={{
            background: "#f3f3f3",
            padding: 15,
            marginTop: 10,
            borderRadius: 10,
          }}
        >
          <b>{i.product.name}</b> — R$ {i.price.toFixed(2)}
          <br />
          Quantidade: {i.quantity}

          <button
            onClick={() => removeItem(i.id)}
            style={{
              float: "right",
              padding: "6px 12px",
              background: "#e33",
              color: "#fff",
              border: "none",
              borderRadius: 6,
              cursor: "pointer",
            }}
          >
            Remover
          </button>
        </div>
      ))}
    </div>
  );
}
