import React, {useEffect,useState} from 'react';
import api from '../api';
export default function Checkout(){
  const [items,setItems]=useState([]);
  const [nome,setNome]=useState('');
  const [endereco,setEndereco]=useState('');
  const [obs,setObs]=useState('');
  useEffect(()=>{ load(); },[]);
  async function load(){ try{ const r=await api.get('/cart'); setItems(r.data);}catch(e){console.error(e);} }
  const total=items.reduce((s,i)=>s+(i.product?.price||0)*(i.quantity||1),0);
  function enviar(){ 
    const phone='55SEUNUMEROAQUI';
    const linhas = items.map(it=>`${it.quantity}x ${it.product?.name} - R$ ${it.product?.price?.toFixed(2).replace('.',',')}`).join('\n');
    const msg = `Pedido\nNome:${nome}\nEnd:${endereco}\n${linhas}\nTotal: R$ ${total.toFixed(2).replace('.',',')}`;
    window.open(`https://api.whatsapp.com/send?phone=${phone}&text=${encodeURIComponent(msg)}`,'_blank');
  }
  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <div>Total: R$ {total.toFixed(2).replace('.',',')}</div>
      <label>Nome</label><input value={nome} onChange={e=>setNome(e.target.value)}/>
      <label>Endere√ßo</label><input value={endereco} onChange={e=>setEndereco(e.target.value)}/>
      <label>Obs</label><textarea value={obs} onChange={e=>setObs(e.target.value)}/>
      <div className="btn" onClick={enviar}>Enviar pelo WhatsApp</div>
    </div>
  );
}
