import React, {useEffect,useState} from "react";
import api,{setAuthToken} from "../api";
export default function Cart(){
  const [items,setItems]=useState([]);
  useEffect(()=>{ load(); },[]);
  async function load(){
    try{
      const token=localStorage.getItem('token'); if(token) setAuthToken(token);
      const r=await api.get('/cart'); setItems(r.data||[]);
    }catch(e){console.error(e)}
  }
  const total=items.reduce((s,it)=>s + (it.product?.price||0)*(it.quantity||1),0);
  return (
    <div className="container">
      <h2>Fechar Pedido</h2>
      {items.length===0 ? <p>Sua sacola est√° vazia</p> : items.map(it=>(
        <div key={it.id} style={{display:'flex',gap:12,alignItems:'center',padding:12,background:'#fff',borderRadius:8,marginBottom:8}}>
          <img src={it.product?.imageUrl||'https://via.placeholder.com/120'} style={{width:96,height:96,objectFit:'cover'}} alt=""/>
          <div style={{flex:1}}><strong>{it.product?.name}</strong><div>R$ {it.product?.price?.toFixed(2).replace('.',',')}</div></div>
        </div>
      ))}
      <div style={{marginTop:12,fontWeight:800}}>Subtotal: R$ {total.toFixed(2).replace('.',',')}</div>
    </div>
  );
}
